import React from 'react';
import './Card.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircle } from '@fortawesome/free-solid-svg-icons';

function Card({ ticket, selectedGroup }) {
  return (
    <div className="card">
      <div className="card-header">
        <p className='tickid'>{ticket.id}</p>
        <div className="card-header-left">
          {selectedGroup === 'user' && (
            <span className="user-symbol">ðŸ‘¤</span>
          )}
          {selectedGroup === 'status' && (
            <span className="status-symbol">{ticket.status}</span>
          )}
          {selectedGroup === 'priority' && (
            <span className="priority-symbol">{getPriorityLabel(ticket.priority)}</span>
          )}
          <h3>{ticket.title}</h3>
        </div>
        {selectedGroup === 'user' && (
          <span className="user-profile-symbol">ðŸ‘¤</span>
        )}
      </div>
      {ticket.tag && (
        <p className='tag'>
          <FontAwesomeIcon icon={faCircle} className='tag-ic' /> 
          {ticket.tag.join(', ')}
        </p>
      )}
    </div>
  );
}

function getPriorityLabel(priorityValue) {
  switch (priorityValue) {
    case 4:
      return 'Urgent';
    case 3:
      return 'High';
    case 2:
      return 'Medium';
    case 1:
      return 'Low';
    case 0:
      return 'No priority';
    default:
      return '';
  }
}

export default Card;
