import React, { useState } from 'react';
import Card from './Card';
import AddCardForm from './AddCardForm';

function Column({ groupKey, tickets, onAddCard }) {
  const [isFormOpen, setIsFormOpen] = useState(false);

  return (
    <div className="column">
      <h2>{groupKey}</h2>
      <button className="add-card-button" onClick={() => setIsFormOpen(true)}>Add Card</button>
      {isFormOpen && <AddCardForm onAddCard={newCard => { onAddCard(groupKey, newCard); setIsFormOpen(false); }} />}
      <div className="cards">
        {tickets.map((ticket, index) => (
          <Card key={ticket.id} index={index} ticket={ticket} />
        ))}
      </div>
    </div>
  );
}

export default Column;
