import React from 'react';

function Card({ ticket, selectedGroup }) {
  return (
    <div className="card">
      <div className="card-header">
        <div className="card-header-left">
        <p>ID: {ticket.id}</p>
          <h3>{ticket.title}</h3>
          {selectedGroup === 'user' && <span className="user-symbol">ðŸ‘¤</span>}
          {selectedGroup === 'status' && <span className="status-symbol">{ticket.status}</span>}
          {selectedGroup === 'priority' && <span className="priority-symbol">{ticket.priority}</span>}
        </div>
        {selectedGroup === 'user' && <span className="user-profile-symbol">ðŸ‘¤</span>}
      </div>
      
      {ticket.tag && <p>Tags: {ticket.tag.join(', ')}</p>}
    </div>
  );
}

export default Card;
