import React from 'react';
import './Card.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircle } from '@fortawesome/free-solid-svg-icons';

function Card({ ticket, selectedGroup }) {
  const renderGroupContent = () => {
    switch (selectedGroup) {
      case 'user':
        return (
          <>
            <h3>{ticket.title}</h3>
          </>
        );
      case 'priority':
        return (
          <>
            <span className="priority-symbol">
              {getPriorityLabel(ticket.priority)}
            </span>
            <h3>{ticket.title}</h3>
          </>
        );
      case 'status':
        return (
          <>
            <span className="status-symbol">{ticket.status}</span>
            <h3>{ticket.title}</h3>
          </>
        );
      default:
        return <h3>{ticket.title}</h3>;
    }
  };

  return (
    <div className="card">
      <div className="card-header">
        <p className='tickid'>{ticket.id}</p>
        <div className="card-header-left">
          {renderGroupContent()}
        </div>
      </div>
      {ticket.tag && (
        <p className='tag'>
          <FontAwesomeIcon icon={faCircle} className='tag-ic' /> 
          {ticket.tag.join(', ')}
        </p>
      )}
    </div>
  );
}

function getPriorityLabel(priorityValue) {
  switch (priorityValue) {
    case 4:
      return 'Urgent';
    case 3:
      return 'High';
    case 2:
      return 'Medium';
    case 1:
      return 'Low';
    case 0:
      return 'No priority';
    default:
      return '';
  }
}

export default Card;
