import React from 'react';
import './Card.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircle } from '@fortawesome/free-solid-svg-icons';

function Card({ ticket, selectedGroup }) {
  const renderGroupContent = () => {
    switch (selectedGroup) {
      case 'user':
        return (
          <>
            <p>{getPriorityLabel(ticket.priority)}</p>
            <p>Priority Index: {ticket.priority}</p>
            <h3>{ticket.title}</h3>
          </>
        );
      case 'priority':
        return (
          <>
            <h3>{ticket.title}</h3>
          </>
        );
      case 'status':
        return (
          <>
            <div className='column-cont'>
              <h3>{ticket.title}</h3>
            <div className='row-cont'>
              <p>{getPriorityLabel(ticket.priority)}</p>
              {ticket.tag && (
                <p className='tag'>
                  <FontAwesomeIcon icon={faCircle} className='tag-ic' /> 
                  {ticket.tag.join(', ')}
                </p>
              )}
            </div>
            </div>
          </>
        );
      default:
        return <h3>{ticket.title}</h3>;
    }
  };

  return (
    <div className="card">
      <div className="card-header">
        <p className='tickid'>{ticket.id}</p>
        <div className="card-header-left">
          {renderGroupContent()}
        </div>
      </div>
    </div>
  );
}

function getPriorityLabel(priorityValue) {
  switch (priorityValue) {
    case 4:
      return 'Urgent';
    case 3:
      return 'High';
    case 2:
      return 'Medium';
    case 1:
      return 'Low';
    case 0:
      return 'No priority';
    default:
      return '';
  }
}

function getPrioritySymbol(priorityValue) {
  switch (priorityValue) {
    case 4:
      return '‚ö°Ô∏è'; // Symbol for Urgent
    case 3:
      return 'üî¥'; // Symbol for High
    case 2:
      return 'üü°'; // Symbol for Medium
    case 1:
      return 'üü¢'; // Symbol for Low
    case 0:
      return '‚óã';  // Symbol for No priority
    default:
      return '';
  }
}


export default Card;
