import React from 'react';
import './Card.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircle } from '@fortawesome/free-solid-svg-icons';

function Card({ ticket, selectedGroup }) {
  const renderGroupContent = () => {
    switch (selectedGroup) {
      case 'user':
        return (
          <>
            <p>{getPriorityLabel(ticket.priority)}</p>
            <p>Priority Index: {ticket.priority}</p>
            <h3>{ticket.title}</h3>
          </>
        );
      case 'priority':
        return (
          <>
            <h3>{ticket.title}</h3>
          </>
        );
      case 'status':
        return (
          <>
            <h3>{ticket.title}</h3>
            <p>{getPriorityLabel(ticket.priority)}</p>
            {ticket.tag && (
              <p className='tag'>
                <FontAwesomeIcon icon={faCircle} className='tag-ic' /> 
                {ticket.tag.join(', ')}
              </p>
            )}
          </>
        );
      default:
        return <h3>{ticket.title}</h3>;
    }
  };

  return (
    <div className="card">
      <div className="card-header">
        <p className='tickid'>{ticket.id}</p>
        <div className="card-header-left">
          {renderGroupContent()}
        </div>
      </div>
      
    </div>
  );
}

function getPriorityLabel(priorityValue) {
  switch (priorityValue) {
    case 4:
      return 'Urgent';
    case 3:
      return 'High';
    case 2:
      return 'Medium';
    case 1:
      return 'Low';
    case 0:
      return 'No priority';
    default:
      return '';
  }
}



export default Card;
