import React from 'react';
import './Card.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircle } from '@fortawesome/free-solid-svg-icons';

function Card({ ticket, selectedGroup }) {
  return (
    <div className="card">
      <div className="card-header">
      <p className='tickid'>{ticket.id}</p>
        <div className="card-header-left">
          <h3>{ticket.title}</h3>
          {selectedGroup === 'user' && <span className="user-symbol">ðŸ‘¤</span>}
          {selectedGroup === 'status' && <span className="status-symbol">{ticket.status}</span>}
          {selectedGroup === 'priority' && <span className="priority-symbol">{ticket.priority}</span>}
        </div>
        {selectedGroup === 'user' && <span className="user-profile-symbol">ðŸ‘¤</span>}
      </div>
      {ticket.tag && (
  <p className='tag'>
    <FontAwesomeIcon icon={faCircle} className='tag-ic' /> {/* Use the correct class name */}
    {ticket.tag.join(', ')}
  </p>
)}

    </div>
  );
}

export default Card;
