import React from 'react';
import './Card.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircle } from '@fortawesome/free-solid-svg-icons';

function Card({ ticket, selectedGroup }) {
  const renderGroupContent = () => {
    switch (selectedGroup) {
      case 'user':
        return (
          <>
            <p>{getPriorityLabel(ticket.priority)}</p>
            <p>Priority Index: {ticket.priority}</p>
            <h3>{ticket.title}</h3>
          </>
        );
      case 'priority':
        return (
          <>
            <h3>{ticket.title}</h3>
          </>
        );
      case 'status':
        return (
          <>
            <div className="priority-container">
              <span className={`priority-symbol ${getPriorityClassName(ticket.priority)}`}>
                {getPrioritySymbol(ticket.priority)}
              </span>
              <p>{getPriorityLabel(ticket.priority)}</p>
            </div>
            <h3>{ticket.title}</h3>
          </>
        );
      // default:
      //   return <h3>{ticket.title}</h3>;
    }
  };

  return (
    <div className="card">
      <div className="card-header">
        <p className='tickid'>{ticket.id}</p>
        <div className="card-header-left">
          {renderGroupContent()}
        </div>
      </div>
      {ticket.tag && (
        <p className='tag'>
          <FontAwesomeIcon icon={faCircle} className='tag-ic' /> 
          {ticket.tag.join(', ')}
        </p>
      )}
    </div>
  );
}

// Define priority symbols and corresponding classes
const prioritySymbols = {
  4: '🔴',
  3: '🟠',
  2: '🟡',
  1: '🟢',
  0: '⚪️',
};

// Get the priority symbol based on the priority value
function getPrioritySymbol(priorityValue) {
  return prioritySymbols[priorityValue] || '';
}

function getPriorityLabel(priorityValue) {
  switch (priorityValue) {
    case 4:
      return 'Urgent';
    case 3:
      return 'High';
    case 2:
      return 'Medium';
    case 1:
      return 'Low';
    case 0:
      return 'No priority';
    default:
      return '';
  }
}

const priorityClasses = {
  4: 'urgent',
  3: 'high',
  2: 'medium',
  1: 'low',
  0: 'no-priority',
};

function getPriorityClassName(priorityValue) {
  return priorityClasses[priorityValue] || '';
}

export default Card;
