import React from 'react';
import './Options.css';

function GroupingOptions({ selectedGroup, onSelectGroup }) {
  return (
    <div className="grouping-options">
      <label>Group By:</label>
      <select value={selectedGroup} onChange={event => onSelectGroup(event.target.value)}>
        <option value="status" className='ops'>Status</option>
        <option value="user" className='ops'>User</option>
        <option value="priority" className='ops'>Priority</option>
      </select>
    </div>
  );
}

export default GroupingOptions;
