import React, { useState } from 'react';

function AddCardForm({ onAddCard }) {
  const [newCard, setNewCard] = useState({
    id: '',        // You can generate this dynamically
    title: '',
    tags: '',
    userId: '',
    priority: '',
    status: '',
  });

  const handleSubmit = event => {
    event.preventDefault();
    if (newCard.title.trim() !== '') {
      onAddCard(newCard);
      setNewCard({
        id: '',
        title: '',
        tags: '',
        userId: '',
        priority: '',
        status: '',
      });
    }
  };

  return (
    <form className="add-card-form" onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="ID"
        value={newCard.id}
        onChange={event => setNewCard({ ...newCard, id: event.target.value })}
      />
      {/* Include input fields for other card properties */}
      <input
        type="text"
        placeholder="Title"
        value={newCard.title}
        onChange={event => setNewCard({ ...newCard, title: event.target.value })}
      />
      <input
        type="text"
        placeholder="Tags"
        value={newCard.tags}
        onChange={event => setNewCard({ ...newCard, tags: event.target.value })}
      />
      {/* ... Repeat for other properties */}
      <button type="submit">Add Card</button>
    </form>
  );
}

export default AddCardForm;
