import React, { useState } from 'react';

function AddCardForm({ onAddCard }) {
  const [title, setTitle] = useState('');
  const [tags, setTags] = useState('');
  const [userId, setUserId] = useState('');
  const [priority, setPriority] = useState('');
  const [status, setStatus] = useState('');

  const handleSubmit = event => {
    event.preventDefault();
    const newCard = {
      id: Math.random().toString(),
      title,
      tags: tags.split(',').map(tag => tag.trim()),
      userId,
      priority: parseInt(priority),
      status,
    };
    onAddCard(newCard);
    // Clear form fields
    setTitle('');
    setTags('');
    setUserId('');
    setPriority('');
    setStatus('');
  };

  return (
    <div className="add-card-form">
      <h3>Add Card</h3>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Title" value={title} onChange={e => setTitle(e.target.value)} required />
        <input type="text" placeholder="Tags (comma separated)" value={tags} onChange={e => setTags(e.target.value)} required />
        <input type="text" placeholder="User ID" value={userId} onChange={e => setUserId(e.target.value)} required />
        <input type="number" placeholder="Priority" value={priority} onChange={e => setPriority(e.target.value)} required />
        <input type="text" placeholder="Status" value={status} onChange={e => setStatus(e.target.value)} required />
        <button type="submit">Add</button>
      </form>
    </div>
  );
}

export default AddCardForm;
