import React, { useState, useEffect } from 'react';
import axios from 'axios';
import KanbanBoard from './components/KanbanBoard';
import Navbar from './components/Navbar';
// import GroupingOptions from './components/GroupingOptions';
// import SortingOptions from './components/SortingOptions';

function App() {
  const [tickets, setTickets] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState('status');
  const [selectedSort, setSelectedSort] = useState('priority');

  useEffect(() => {
    axios.get('https://api.quicksell.co/v1/internal/frontend-assignment')
      .then(response => {
        setTickets(response.data.tickets);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const handleAddCard = (groupKey, newCard) => {
    const updatedTickets = {
      ...tickets,
      [groupKey]: [...tickets[groupKey], newCard],
    };
    setTickets(updatedTickets);
  };

  return (
    <div className="App">
      <Navbar
        selectedGroup={selectedGroup}
        onSelectGroup={setSelectedGroup}
        selectedSort={selectedSort}
        onSelectSort={setSelectedSort}
      />
      <KanbanBoard tickets={tickets} onAddCard={handleAddCard} selectedGroup={selectedGroup} selectedSort={selectedSort} />
      </div>
  );
}

export default App;
